mapper.select:
select COUNT(DISTINCT log_time,user_name,event_type) from bind_yb_gain where 1=1
and event_type in (#{item}) 
and user_name = #{userName} 
and date_format(log_time,'%Y-%m-%d')  >=  #{startTime} 
and date_format(log_time,'%Y-%m-%d')  <=  #{endTime}

select event_type as eventType ,sum(yb) yb ,sum(current_yb) currentYb ,sum(times) times ,log_day as logDay from bind_yb_gain where 1=1 and event_type in
#{item}
and log_time>= #{startTime} and log_time<= #{endTime} group by log_day,event_type order by log_day desc,yb desc

