mapper.select:
select 
	user_name as userName,
	event_type as eventType,
	goods_id as goodsId,
	goods_name as goodsName,
	count,
	log_time as logTime
from consume_prop_detail_${month}
where 1=1
and user_name = #{userName} 
and event_type in (#{item})
and goods_name = #{goodsName} 
and date_format(log_time, '%d %H:%i')  >=  #{startTime} 
and date_format(log_time, '%d %H:%i')  <=  #{endTime}

select count(1)
from consume_prop_detail_${month}
where 1=1
and user_name = #{userName} 
and event_type in (#{item})
and goods_name = #{goodsName} 
and date_format(log_time, '%d %H:%i')  >=  #{startTime} 
and date_format(log_time, '%d %H:%i')  <=  #{endTime}
