mapper.select:
         select
            user_name as userName,
            event_type as eventType,
       		yb,
       	    current_yb as currentyb,
       		log_time  logTime
		from gain_yb_detail_${month}
		where 1=1
         and user_name =#{userName} 
          and event_type in (#{item})  
		and date_format(log_time, '%d %H:%i')  >=  #{startTime} 
		and date_format(log_time, '%d %H:%i')  <=  #{endTime}

		select count(1) from gain_yb_detail_${month}
		where 1=1
         and user_name =#{userName} 
          and event_type in (#{item})  
		and date_format(log_time, '%d %H:%i')  >=  #{startTime} 
		and date_format(log_time, '%d %H:%i')  <=  #{endTime}
