mapper.select:
	    select 
	    	log_time as logTime, 
	    	type,
	    	role_name as roleName,
	    	to_role_name as toRoleName,
	    	msg as msg  
		FROM gm_chat
        where 1=1
        and role_name =#{roleName} 
        and type =#{type} 
        and to_role_name =#{toRoleName} 
		and date_format(log_time,'%Y-%m-%d')  >=  #{startTime} 
		and date_format(log_time,'%Y-%m-%d')  <=  #{endTime}

select count(1) FROM gm_chat where 1=1
and role_name =#{roleName} 
and type =#{type} 
and to_role_name =#{toRoleName} 
and date_format(log_time,'%Y-%m-%d')  >=  #{startTime} 
and date_format(log_time,'%Y-%m-%d')  <=  #{endTime}
