mapper.select:
	    select log_time as logTime, 
	    type as type,
	    role_name as roleName,
	    to_role_name as toRoleName,
	    msg as msg  
	    from normal_chat
        where 1=1
         and role_name =#{roleName} 
         and type =#{type} 
         and to_role_name =#{toRoleName} 
         and log_time> #{startTime} 
         and  log_time< #{endTime} 
       order by log_time desc

select count(1)
   from normal_chat
   where 1=1
   and role_name =#{roleName} 
   and type =#{type} 
   and to_role_name =#{toRoleName} 
   and log_time> #{startTime} 
   and  log_time< #{endTime}
