mapper.select:
	       select count(1)
           from yb_incr
           where 1=1
            and user_name =#{userName} 
             and event_type in 

               #{item}  


            and log_day> #{startTime} 
            and log_day< #{endTime}

select event_type as eventType ,sum(yb) yb ,sum(times) times ,log_day as logDay from yb_incr where 1=1 and event_type in
#{item}
and log_time >= #{startTime} and log_time <= #{endTime} group by log_day,event_type order by log_day desc,yb desc

	    select count(DISTINCT log_day,event_type) 
	    from yb_incr
	    where 1=1
         and event_type in 

                  #{item}  


        and log_time >= #{startTime} 
        and log_time <= #{endTime}

