and server_id in #{item}
and date >= #{startTime} and date <= #{endTime} group by date order by date desc ) tb1 union all select "合计" as date ,sum(createCount) createCount ,sum(dCount) dCount ,avg((1-(dCount-createCount)/lsDcount)*100) lost ,avg(accu) accu ,avg(accu*24*60/dCount) dt ,sum(cnt) cnt ,sum(distCount) distCount ,avg(distCount/dCount*100) pl ,avg(allRmb) allRmb ,avg(allRmb/distCount) arpu from ( select DATE_FORMAT(date,'%Y-%m-%d') date ,log_time logTime ,sum(create_count) createCount ,sum(d_count) dCount ,sum(accu) accu ,sum(cnt) cnt ,sum(dist_count) distCount ,sum(all_rmb) allRmb ,max(pccu) pccu ,sum(yb_incr) ybIncr ,sum(yb_consume) ybConsume ,sum(ls_dcount) lsDcount from servers_integration_report where 1=1
