mapper.select:
SELECT id, server_id as serverId, receiver_names receiverNames, title, content, attachments, jb, yb, delay_hours as delayHours, create_time as createTime, user_name as userName, check_name as checkName, global_send as globalSend, state FROM system_email where global_send is not null and state = #{state} and title like concat('%',#{title},'%') and global_send = #{globalSend} and date_format(create_time,'%Y-%m-%d') >= #{startTime} and date_format(create_time,'%Y-%m-%d') <= #{endTime}
and create_by in (#{item})
order by create_time desc

SELECT count(1) FROM system_email where global_send is not null
and state = #{state} 
and title like concat('%',#{title},'%') 
and global_send = #{globalSend} 
and date_format(create_time,'%Y-%m-%d')  >=  #{startTime} 
and date_format(create_time,'%Y-%m-%d')  <=  #{endTime} 

	and create_by in (#{item})

select  count(1) from system_email where global_send is not null
and state in (1,2) 
and state = #{state} 
and global_send = #{globalSend} 
and title like concat('%',#{title},'%') 
and date_format(create_time,'%Y-%m-%d')  >=  #{startTime} 
and date_format(create_time,'%Y-%m-%d')  <=  #{endTime}

mapper.update:
		update system_email set check_name=#{sessionUser.loginName}, state=#{state} 
		where id in

            #{item}
